import React, { useState } from 'react';
import genId from '../utils/genId';
import apiccHelper from '../helpers/apicc';

const Vote = () => {
  const [toHash, setToHash] = useState("");
  const [result, setResult] = useState("");
  const [showProgres, setShowProgres] = useState(false);
  const activate = () => {
      setShowProgres(true);
  };
  return (
  <div class="container-fluid">
    <div class="row">
    <div class="col-6">
      <div class="col-sm-11 offset-sm-1">
      <p/>
     <p><h3>  Formulario Votación</h3></p>
      </div>
    <form>
      <div class="form-group">
        <div class="col-md-11 offset-md-1">
        <label class="col-md-3">Seleccione candidato</label>
          <div class="custom-control custom-radio custom-control-inline">
            <input name="radio" id="radio_0" type="radio" class="custom-control-input" value="rabbit"/>
            <label for="radio_0" class="custom-control-label">Candidato 1</label>
          </div>
          <div class="custom-control custom-radio custom-control-inline">
            <input name="radio" id="radio_1" type="radio" class="custom-control-input" value="duck"/>
            <label for="radio_1" class="custom-control-label">Candidato 2</label>
          </div>
      </div>
      </div>
        <div class="col-sm-11 offset-sm-1">
          <div class="row">
            <div class="col-5 col-sm-4">
              <label class="col-lg" >Ingrese su identificador secreto</label>
            </div>
            <div class="col-5 col-sm-6">
              <input id="text" name="text" type="text" required="required" class="form-control"/>
            </div>
            <div class="col-2 col-sm-2">
              <button name="submit" type="submit" class="btn btn-primary" onClick={activate}>Enviar</button>
            </div>
          </div>
        </div>
      </form>
    </div>
    <div class="col-6">
      { showProgres? <Progres /> : null }
    </div>
      </div>
  </div>
  )
};

const Progres = () => {
    return(
      <div class="card">
        <ul class="list-group list-group-flush">
          <li class="list-group-item" id="step1">An item</li>
          <li class="list-group-item" id="step2">A second item</li>
          <li class="list-group-item" id="step3">A third item</li>
        </ul>
      </div>

    )
};

export default Vote;
