import HASH from '../codes/hash';
import CONST from '../config/constant';
import apicc from '../helpers/apicc';

import { initialize } from 'zokrates-js';
const voting = async (toHash, setter, results, vote) => {
    console.log(toHash);
    const formatter = w => JSON.parse(w).map((x) =>'0x'+ x.toString(16).padStart(8,"0"));
    const hasher = await import('witness');
    setter(1);
    const to_witness = hasher.genHash(toHash);
    const vote_hash = hasher.genHash(vote);
    const nullifier = hasher.genNullifier(toHash);
    const format_witness = formatter(to_witness);
    const format_vote = formatter(vote_hash);
    const format_nullifier = formatter(nullifier);
    setter(2);
    const zok = await initialize();
    setter(3);
    const systemArtifacts = await apicc.getArtifacts();
    setter(4);
    const vote_sign = hasher.voteSign(vote, systemArtifacts.voteConst.value);
    const format_sign = formatter(vote_sign);
    const { witness, out } = zok.computeWitness(systemArtifacts.artifacts,
        [
            format_witness,
            systemArtifacts.voteRoll,
            format_vote,
            systemArtifacts.voteConst.hash,
            format_nullifier,
            format_sign,
        ],
    );
    setter(5);
    console.log(out);
    const proof = zok.generateProof(systemArtifacts.artifacts.program, witness, systemArtifacts.keyPairs.pk);
    setter(6);
    console.log(proof);
    console.log(format_witness);
    results(` proof: ${JSON.stringify(proof)}`);
}

export default voting;
