import React, { useState } from 'react';
import genId from '../utils/genId';
import apiccHelper from '../helpers/apicc';

const Setup = () => {
  const [toHash, setToHash] = useState("");
  const [nbrVoters, setNbrVoters] = useState("");
  const [result, setResult] = useState("");
  const [waiting, setWaiting] = useState(false);

  const init = async () => {
    setWaiting(true);
    const msg = await apiccHelper.setup();
    setWaiting(false);
    console.log(msg);
    alert(msg);
  }
  return (
      <div class="container-fluid">
        <div class="row">
          <div class="col-11">
            <h5 class="display-4"> Administrar</h5>
          </div>
          <div class="col-6 offset-1">
            <div class="row">
              <h3 class="text-muted"><small>Ingrese constante del sistema </small></h3>
            </div>
            <div class ="row">
              <div class="col-5 align-self-start">
                <input class="form-control" id="hasherInput" value={toHash} onChange={evt => setToHash(evt.target.value)} />
              </div>
              <div class= "col-4">
               <button name="submit" type="submit" class="btn btn-primary" onClick={init}>Inicializar</button>
             </div>
             <div class="col-3">{ waiting?
                <div class="d-flex align-items-center">
                  <strong>Inicializando el Proceso</strong>
                  <div class="spinner-border ml-auto" role="status" aria-hidden="true"></div>
                </div>
                :null}
              </div>
            </div>
            <div class="row">
              <div class="col-5">
                <h3 class="text-muted"><small>Inicializar participantes </small></h3>
              </div>
              <div class="col-2">
                <input type="number" min="0" max="9" class="form-control" value={nbrVoters} onChange={evt => setNbrVoters(evt.target.value)} /> 
              </div>
            </div>
           </div>
          <div class="col-5">
          </div>
        </div>
      </div>
  )
};
export default Setup;
