import axios from 'axios';
import CONST from '../config/constant';

const apicc = {};

apicc.registerId = (id) => {
    return id;
};
apicc.getArtifacts = async (voteConstant, numberVoters) => {
    const artifacts = await axios.post(
        `${CONST.API.url}/artifacts`,
        {
          voteConstant,
          numberVoters,
        },
    );
    return artifacts.data.payload;
};
apicc.setup = async () => {
   const res = await axios.get(
      `${CONST.API.url}/init`,
   );
   return res.data.message;
}

apicc.sendVote = () => {
};

export default apicc;
