import React, { useState } from 'react';
import './App.css';
import axios from 'axios';


const App = () => {
const wit = import('witness').then( genHash => genHash);
console.log(wit);
  const [toHash, setToHash] = useState("");
  // load the zokrates compiled function file (out) needed for the proof generation
  return (
    <div className="App">
      <header className="App-header">
          <div>
          <p>
          Insert secret
          </p>
          </div>
          //ask for the secret
          <input value={toHash} onChange={evt => setToHash(evt.target.toHash)} />
        <button onClick={ async () => {
            const hashed = wit(toHash);
            console.log(hashed);
        }
        }>
         </button>
      </header>
    </div>
  );
};
export default App;
