import React, { useState } from 'react';
import './App.css';
import axios from 'axios';
import witness from '../../wasm-build/witness';

const App = () => {

  const [toHash, setToHash] = useState("");
  const [result, setResult] = useState("");
  // load the zokrates compiled function file (out) needed for the proof generation
  return (
    <div className="App">
      <header className="App-header">
          <div>
          <p>
          Insert secret
          </p>
          //ask for the secret
          <input value={toHash}> onChange={evt => setToHash(evt.target.toHash)} />
          </input>
          </div>
        <button onClick={ async () => {
            const hashed = witness.genHash(toHash);
            console.log(hashed);
        }
        }>
         </button>
      </header>
    </div>
  );
};
export default App;
