import React, { useState } from 'react';
import './App.css';
import { initialize } from 'zokrates-js';
import HASH from './codes/hash';

const App = () => {
  const [toHash, setToHash] = useState("");
  // load the zokrates compiled function file (out) needed for the proof generation
  return (
    <div className="App">
      <header className="App-header">
          <div>
          <p>
          Insert secret
          </p>
          </div>
          <input value={toHash} onChange={evt => setToHash(evt.target.value)} />
        <button onClick={ async () => {
            const hasher = await import('witness');
            const to_witness = hasher.genHash(toHash);
            console.log(to_witness);
            const zok = await initialize();
            const artifacts = zok.compile(HASH);
            const { witness, output} = zok.computeWitness(artifacts, to_witness)

           console.log(witness);
        }
        }>
        Calcular
         </button>
      </header>
    </div>
  );
};
export default App;
