import HASH from '../codes/hash';
import CONST from '../config/constant';
import apicc from '../helpers/apicc';

import { initialize } from 'zokrates-js';
const voting = async (toHash, setter, results) => {
    console.log(toHash);
    const formatter = w => JSON.parse(w).map((x) =>'0x'+ x.toString(16).padStart(8,"0"));
    const hasher = await import('witness');
    setter(1);
    const to_witness = hasher.genHash(toHash);
    setter(2);
    const to_pub = hasher.genId(toHash);
    setter(3);
    const nullifier = hasher.genNullifier(toHash);
    console.log(JSON.parse(to_witness));
    const format_witness = formatter(to_witness);
    const format_pub = formatter(to_pub);
    const zok = await initialize();
    setter(4);
    const systemArtifacts = await apicc.getArtifacts();
    console.log(systemArtifacts.artifacts);
    setter(5);
    const { witness, output } = zok.computeWitness(systemArtifacts.artifacts, [format_witness, format_pub]);
    setter(6);
    const keyPair = zok.setup(systemArtifacts.artifacts.program);
    setter(7);
    const proof = zok.generateProof(systemArtifacts.artifacts.program, witness, systemArtifacts.keyPairs.pk);
    setter(8);
    console.log(proof);
    console.log(format_witness);
    results(`witness: [${format_witness}],\n
    public: [${format_pub}]
    proof: ${proof}
`);
}

export default voting;
