import HASH from '../codes/hash';
import { initialize } from 'zokrates-js';
const voting = async (toHash, setter, results) => {
    console.log(toHash);
    const formatter = w => JSON.parse(w).map((x) =>'0x'+ x.toString(16).padStart(8,"0"));
    const hasher = await import('witness');
    setter(1);
    const to_witness = hasher.genHash(toHash);
    setter(2);
    const to_pub = hasher.genId(toHash);
    setter(3);
    console.log(JSON.parse(to_witness));
    const format_witness = formatter(to_witness);
    const format_pub = formatter(to_pub);
    const zok = await initialize();
    const artifacts = zok.compile(HASH);
    const { witness, output } = zok.computeWitness(artifacts, [format_witness, format_pub]);
    const keyPair = zok.setup(artifacts.program);
    const proof = zok.generateProof(artifacts.program, witness, keyPair.pk);
    console.log(format_witness);
    results(`witness: [${format_witness}],\n
    public: [${format_pub}]`);
}

export default voting;
