import React, { useState } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import genId from './utils/genId';
import Navbar from './components/Navbar';
const App = () => {
  const [toHash, setToHash] = useState("");
  const [result, setResult] = useState("");
  // load the zokrates compiled function file (out) needed for the proof generation
  return (
    <div>
      <Navbar />
      <div class="container">
        <div class="col-sm">
          <p>
          Insert secret
          </p>
          </div>
          <input value={toHash} onChange={evt => setToHash(evt.target.value)} />
        <button onClick={ async () => {
            const outHash = await genId(toHash);

            setResult(outHash);
        }
        }>
        Calcular
         </button>
      <div>
      <p>
      {result}
      </p>
      </div>
    </div>
    </div>
  );
};
export default App;
